"use strict";(self.webpackChunkphasetwo_docs=self.webpackChunkphasetwo_docs||[]).push([[53],{1109:function(e){e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"docs":[{"type":"category","label":"Introduction","items":[{"type":"link","label":"Keycloak","href":"/docs/introduction/keycloak","docId":"introduction/keycloak"},{"type":"link","label":"Open Source","href":"/docs/introduction/open-source","docId":"introduction/open-source"},{"type":"link","label":"Documentation","href":"/docs/introduction/documentation","docId":"introduction/documentation"},{"type":"link","label":"Software Development Kits","href":"/docs/introduction/sdks","docId":"introduction/sdks"}],"collapsed":true,"collapsible":true,"href":"/docs/introduction/"},{"type":"category","label":"Getting Started","items":[{"type":"link","label":"Sample Application","href":"/docs/getting-started/sample","docId":"getting-started/sample"},{"type":"link","label":"Customizing UI","href":"/docs/getting-started/customizing-ui","docId":"getting-started/customizing-ui"}],"collapsed":true,"collapsible":true,"href":"/docs/getting-started/"},{"type":"category","label":"Securing Applications","items":[{"type":"link","label":"Javascript","href":"/docs/securing-applications/javascript","docId":"securing-applications/javascript"},{"type":"link","label":"React","href":"/docs/securing-applications/react","docId":"securing-applications/react"}],"collapsed":true,"collapsible":true,"href":"/docs/securing-applications/"},{"type":"category","label":"Account Management","items":[{"type":"link","label":"Hosted","href":"/docs/account-management/hosted","docId":"account-management/hosted"},{"type":"link","label":"Account Management API","href":"/docs/account-management/api","docId":"account-management/api"}],"collapsed":true,"collapsible":true,"href":"/docs/account-management/"},{"type":"category","label":"SSO","items":[{"type":"link","label":"SSO Without Auth","href":"/docs/sso/sso-without-auth","docId":"sso/sso-without-auth"},{"type":"link","label":"Magic Link Authentication","href":"/docs/sso/magic-link","docId":"sso/magic-link"}],"collapsed":true,"collapsible":true,"href":"/docs/sso/"},{"type":"category","label":"User Migration","items":[{"type":"link","label":"User Migration API","href":"/docs/user-migration/api","docId":"user-migration/api"}],"collapsed":true,"collapsible":true,"href":"/docs/user-migration/"},{"type":"category","label":"Audit Logs","items":[{"type":"link","label":"Access","href":"/docs/audit-logs/access","docId":"audit-logs/access"},{"type":"link","label":"Admin","href":"/docs/audit-logs/admin","docId":"audit-logs/admin"},{"type":"link","label":"System","href":"/docs/audit-logs/system","docId":"audit-logs/system"},{"type":"link","label":"Audit Log API","href":"/docs/audit-logs/api","docId":"audit-logs/api"},{"type":"link","label":"Webhooks","href":"/docs/audit-logs/webhooks","docId":"audit-logs/webhooks"}],"collapsed":true,"collapsible":true,"href":"/docs/audit-logs/"},{"type":"category","label":"Organizations","items":[{"type":"link","label":"Attributes","href":"/docs/organizations/attributes","docId":"organizations/attributes"},{"type":"link","label":"Membership","href":"/docs/organizations/membership","docId":"organizations/membership"},{"type":"link","label":"Invitations","href":"/docs/organizations/invitations","docId":"organizations/invitations"},{"type":"link","label":"Roles","href":"/docs/organizations/roles","docId":"organizations/roles"},{"type":"link","label":"Portal","href":"/docs/organizations/portal","docId":"organizations/portal"}],"collapsed":true,"collapsible":true,"href":"/docs/organizations/"},{"type":"category","label":"Connect","items":[{"type":"link","label":"Setup","href":"/docs/connect/setup","docId":"connect/setup"}],"collapsed":true,"collapsible":true,"href":"/docs/connect/"}]},"docs":{"about":{"id":"about","title":"About Us","description":"Mission"},"account-management/api":{"id":"account-management/api","title":"Account Management API","description":"TBD","sidebar":"docs"},"account-management/hosted":{"id":"account-management/hosted","title":"Hosted","description":"The hosted account management experience can be easily linked to from your application. Branding is automatic using the same variables for customizing the login UI.","sidebar":"docs"},"account-management/index":{"id":"account-management/index","title":"Account Management","description":"Giving your users a facility to easily manage their account details is an important component of a comprehensive authentication service. Phase Two has two options for providing this functionality.","sidebar":"docs"},"affiliate":{"id":"affiliate","title":"Phase Two Affiliate Program","description":"Coming soon!"},"api-description":{"id":"api-description","title":"api-description","description":"This is a REST API reference for the Phase Two Keycloak custom resources. These are extensions to the standard Keycloak Admin REST API."},"audit-logs/access":{"id":"audit-logs/access","title":"Access","description":"Phase Two records access event types that pertain to end users use of the Phase Two and customer systems. These are primarily registration, login and account management actions.","sidebar":"docs"},"audit-logs/admin":{"id":"audit-logs/admin","title":"Admin","description":"Actions performed from the Phase Two dashboard or using the administrative API are recorded as events.","sidebar":"docs"},"audit-logs/api":{"id":"audit-logs/api","title":"Audit Log API","description":"Customers can use the existing audit logging mechanism to include their own application\'s events. There is a single API method that will consume events in the representation below and make them available for searching, filtering and exporting.","sidebar":"docs"},"audit-logs/index":{"id":"audit-logs/index","title":"Audit Logs","description":"An audit log is a chronological record of security-relevant actions that occur in a system. It is used to store evidence of a specific operation, procedure or event. Many enterprise customers of SaaS businesses require an audit log of access, administrative and system events. Companies that have compliance requirements (e.g. SOC 2) may be required to keep such logs in order to produce an official record when obligated to produce proof of compliance. Additionally, developers use audit logs to do root cause analysis of how a complex system produced a failure or arrived in an unexpected state.","sidebar":"docs"},"audit-logs/system":{"id":"audit-logs/system","title":"System","description":"System event types are reported by Phase Two to give the customer information on operational issues with the Phase Two system. Things like system maintenance, scheduled downtime, version updates, outage notifications and more will be published for this event type.","sidebar":"docs"},"audit-logs/webhooks":{"id":"audit-logs/webhooks","title":"Webhooks","description":"Webhooks give application developers the ability to listen for all audit events using an HTTP endpoint. Events are sent to your endpoint using the same format as they are submitted in the audit API.","sidebar":"docs"},"careers":{"id":"careers","title":"Careers at Phase Two","description":"Open positions"},"connect/index":{"id":"connect/index","title":"Connect","description":"Phase Two Connect is currently available by invitation only. Contact us for more information.","sidebar":"docs"},"connect/setup":{"id":"connect/setup","title":"Setup","description":"The standard Phase Two Docker image contains the base version of Phase Two Connect by default. This restricts the IdP onboarding wizards to the generic SAML, OIDC and LDAP protocol wizards. If you are a licensee with a support agreement, talk to your customer success representative about accessing your private image registry.","sidebar":"docs"},"getting-started/customizing-ui":{"id":"getting-started/customizing-ui","title":"Customizing UI","description":"Customizing UI for login screens","sidebar":"docs"},"getting-started/index":{"id":"getting-started/index","title":"Getting Started","description":"","sidebar":"docs"},"getting-started/sample":{"id":"getting-started/sample","title":"Sample Application","description":"Most users will want to get started quickly with a sample of how to secure an application. Once you have registered for a Phase Two account, you can clone this repository to see a demonstration of authentication in action.","sidebar":"docs"},"introduction/documentation":{"id":"introduction/documentation","title":"Documentation","description":"In addition to the guides here and in the API documentation, users are encouraged to use the Keycloak documentation for the purpose of learning about Keycloak\'s capabilities, server administration techniquies, and guides for securing applications.","sidebar":"docs"},"introduction/index":{"id":"introduction/index","title":"Introduction","description":"Welcome to Phase Two. We have built a toolkit for some of the most common features and pain points you will encounter when building your SaaS application. Initially, we have focused on authentication and authorization use cases. Our goal is to build secure and easy-to-use tools that will accelerate your time to market and adoption by enterprise customers.","sidebar":"docs"},"introduction/keycloak":{"id":"introduction/keycloak","title":"Keycloak","description":"Phase Two is based on the Keycloak Open Source Identity and Access Management system, built and maintained by Red Hat.","sidebar":"docs"},"introduction/open-source":{"id":"introduction/open-source","title":"Open Source","description":"The core extensions to Keycloak that Phase Two is built on will always be open source so that you can migrate to your own Keycloak deployment. Below is a list of the relevant extensions and their current status.","sidebar":"docs"},"introduction/sdks":{"id":"introduction/sdks","title":"Software Development Kits","description":"Much of the functionality described here is documented in our API section. Our API is built as extensions to the standard Keycloak Admin REST API, and can be used in a similar way.","sidebar":"docs"},"organizations/attributes":{"id":"organizations/attributes","title":"Attributes","description":"Attributes functionality provides key-value storage of Organization attributes that can be used in your application to customize experience. The ability to manage attributes is available in the Organizations tab of the Admin UI.","sidebar":"docs"},"organizations/index":{"id":"organizations/index","title":"Organizations","description":"The Organizations feature represents an significant enhancement to standard Keycloak that allows business-to-business (B2B) customers to better manage their partners and customers, and to customize the ways that end-users access their applications. Phase Two customers can use Organizations to:","sidebar":"docs"},"organizations/invitations":{"id":"organizations/invitations","title":"Invitations","description":"Invitations provide a way to allow Keycloak and organization administrators to invite new members to the organization.","sidebar":"docs"},"organizations/membership":{"id":"organizations/membership","title":"Membership","description":"Users who are associated with an organization are considered members. The relationship of users to organizations can be managed in the Organizations tab of the Admin UI. Invitations also provide a way to allow organization administrators to invite new members to the organization. If you are associating an identity provider with an organization, all users who authenticate through an associated identity provider will automatically be added as members to the organization.","sidebar":"docs"},"organizations/portal":{"id":"organizations/portal","title":"Portal","description":"The Organization Portal allows members of an organization with appropriate permissions to self-manage their organization. This includes details, attributes, memberships, verified domains, identity providers, as well as view information about events that have occured relevant to access.","sidebar":"docs"},"organizations/roles":{"id":"organizations/roles","title":"Roles","description":"Members of an organization can have role assignments that are specific to that organization. These are separate from Keycloak realm and client roles, and do not inherit from them. There are a set of default roles that control access to functionality within Phase Two, and additional roles can be added for your application purposes. Role creation, management and assigment can be done in the Organizations tab of the Admin UI.","sidebar":"docs"},"privacy":{"id":"privacy","title":"Phase Two Privacy Policy","description":"Last updated: July 05, 2022"},"securing-applications/index":{"id":"securing-applications/index","title":"Securing Applications","description":"Phase Two is an implementation of the OpenID Connect specification. That means, no custom libraries or code are required Your applications and services can be secured using any compliant OpenID Connect Relying Party library. There are lists maintained by the OpenID Foundation of client libraries.","sidebar":"docs"},"securing-applications/javascript":{"id":"securing-applications/javascript","title":"Javascript","description":"Most modern applications are being built as single-page apps. The easiest way to secure these is with the Javascript keycloak-js library. If you are using a package manager like NPM, you can use it from there. If you are importing it direclty, the library is served by the server at https://app.phasetwo.io/auth/js/keycloak.js","sidebar":"docs"},"securing-applications/react":{"id":"securing-applications/react","title":"React","description":"Many SPAs use a framework such as React to simplify the creation of interactive experiences. We suggest the use of the open source react-keycloak library to make securing React applications easier","sidebar":"docs"},"sso/index":{"id":"sso/index","title":"SSO","description":"SSO enables authentication via an organization\u2019s Identity Provider (IdP), such as Google Workspace or Okta instead of managing usernames and passwords. Phase Two implementations of IdP connections support SAML and OpenID Connect standard protocols.","sidebar":"docs"},"sso/magic-link":{"id":"sso/magic-link","title":"Magic Link Authentication","description":"Magic Link Authentication allows your users to log in with a special token encoded in an email. Phase Two supports two different mechanisms of creating this link.","sidebar":"docs"},"sso/sso-without-auth":{"id":"sso/sso-without-auth","title":"SSO Without Auth","description":"Many Phase Two customers use their own authentication and user management systems, and only rely on Phase Two for its comprehensive SSO support. It is not required to use both in order to get the full power of our SSO integrations and customer self-management portal.","sidebar":"docs"},"terms":{"id":"terms","title":"Phase Two Terms and Conditions","description":"Last updated: July 05, 2022"},"user-migration/api":{"id":"user-migration/api","title":"User Migration API","description":"Prerequisites","sidebar":"docs"},"user-migration/index":{"id":"user-migration/index","title":"User Migration","description":"User migration is an optional feature that can be installed in your realm by request. Customers who have legacy user databases they wish to import can use this feature as part of initial or ongoing migration to Phase Two. We use a variant of this Keycloak user migration extension.","sidebar":"docs"}}}')}}]);